const o=[{text:"Rojo",color:"#890000",buttonsColor:"gold"},{text:"Turquesa",color:"#19B1AC",buttonsColor:"#ccc"},{text:"Negro",color:"#111",buttonsColor:"#bbb"},{text:"Azul",color:"#001F91",buttonsColor:"#ddd"},{text:"Amarillo",color:"#d68111",buttonsColor:"#1836a3"}],t=document.querySelector(".change-color"),r=document.querySelector(".tamagotchi");t.innerHTML=o.map((o=>`<button style="--color: ${o.color}; --text-color: ${o.buttonsColor}">\n    <span>${o.text}</span>\n  </button>`)).join("");Array.from(document.querySelectorAll("button")).forEach(((t,e)=>{t.addEventListener("click",(()=>{r.style.setProperty("--body-color",o[e].color),r.style.setProperty("--buttons-color",o[e].buttonsColor)}))}));
